pragma solidity ^0.4.16;

contract Metadata {

    /*** Events ****************/
    event MetadataPublishLog(
    bytes title,
    bytes content_hash,
    bytes license,
    bytes extras,
    bytes dna,
    bytes signature,
    address pk
    );

    /*** Functions ****************/

    function publish(
    bytes title,
    bytes content_hash,
    bytes license,
    bytes extras,
    bytes dna,
    bytes signature,
    address pk
    ) internal {
        MetadataPublishLog(title, content_hash, license, extras, dna, signature, pk);
    }

    function sign_publish(
    bytes title,
    bytes content_hash,
    bytes license,
    bytes block_hash,
    bytes extras,
    bytes signature
    ) public returns (bytes) {

        bytes32 r = bytesToBytes32(slice(signature, 0, 32));
        bytes32 s = bytesToBytes32(slice(signature, 32, 32));
        byte v = slice(signature, 64, 1)[0];

        // 得到公钥地址
        address pk = ecrecoverDecode(keccak256(title, content_hash, license), r, s, v);

        // 计算dna
        bytes memory dna = Bytes32toBytes(keccak256(signature, block_hash));

        publish(title, content_hash, license, extras, dna, signature, pk);

        return dna;
    }

    //bytes转换为bytes32
    function bytesToBytes32(bytes memory source) internal returns (bytes32 result) {
        assembly {
        result := mload(add(source, 32))
        }
    }

    function bytes32ToString(bytes32 x) constant private returns (string) {
        bytes memory bytesString = new bytes(32);
        uint charCount = 0;
        for (uint j = 0; j < 32; j++) {
            byte char = byte(bytes32(uint(x) * 2 ** (8 * j)));
            if (char != 0) {
                bytesString[charCount] = char;
                charCount++;
            }
        }
        bytes memory bytesStringTrimmed = new bytes(charCount);
        for (j = 0; j < charCount; j++) {
            bytesStringTrimmed[j] = bytesString[j];
        }
        return string(bytesStringTrimmed);
    }

    //使用ecrecover恢复公匙
    function ecrecoverDecode(bytes32 sig_mag, bytes32 r, bytes32 s, byte v1) internal returns (address addr){
        uint8 v = uint8(v1) + 27;
        addr = ecrecover(sig_mag, v, r, s);
    }

    function Bytes32toBytes(bytes32 x) internal returns (bytes) {
        bytes memory c = new bytes(32);
        for (uint i = 0; i < 32; i++) {
            c[i] = x[i];
        }
        return c;
    }


    //将原始数据按段切割出来指定长度
    function slice(bytes memory data, uint start, uint len) internal returns (bytes){
        bytes memory b = new bytes(len);

        for (uint i = 0; i < len; i++) {
            b[i] = data[i + start];
        }

        return b;
    }
}
