pragma solidity ^0.4.0;

contract MetadataInterface {
    /*** Events ****************/
    event MetadataPublishLog(string title, string content_hash, string license, string extras, string dna, bytes signature, address pk);
    event LikeLog(string _dna);
    event CommentLog(string _dna);
    event ForwardLog(string _dna,string _community);
    event ReproduceLog(string _dna,string _link);

    /*** Functions ****************/
    function publish(string title,string content_hash,string license,string extras,string dna,bytes signature,address pk) public;
    function sign_publish(string title,string content_hash,string license,string blockhash,string extras,bytes signature,address pk) public returns (bool);
    function like(string _dna) public;
    function comment(string title,string content_hash,string license,string blockhash,string extras,bytes signature,address pk) public returns (bool);
    function forward(string _dna, string _community) public;
    function reproduce(string _dna, string _link) public;
    function bytesToBytes32(bytes memory source) returns (bytes32 result);
    function ecrecoverDecode(bytes32 sig_mag, bytes32 r, bytes32 s, byte v1) returns (address addr);
    function slice(bytes memory data, uint start, uint len) returns (bytes);
}
