pragma solidity ^0.4.8;

import "./StandardToken.sol";
import "../contract/Ownable.sol";
import "../interface/PrimasStorageInterface.sol";

contract PrimasToken is StandardToken {

    /**** Properties ************/

    string public name = "Primas Token V1";
    uint public decimals = 18;
    string public symbol = "PST";
    string public version = "V1.0";
    address initialOwner;
    uint initialAmount;
    uint deployTime;

    // 增发
    uint lastInflationDayStart;


    // 奖励池
    uint incentivesPool;
    mapping(address => bool) public incentivesOperators;

    // 创建作者身份需锁定一部分 PST
    mapping(address => uint) private autherStorage;

    // 发布文章需锁定一部分 PST
    mapping(bytes32 => uint) private articleStorage;
    // 锁定时间
    mapping(bytes32 => uint) private tokenLockStorage;

    /*** Contracts **************/
    PrimasStorageInterface primaStorage = PrimasStorageInterface(0);



    /*** Events ****************/

    event UserDeposit (
        address indexed _from,
        string  poolStakingTimeID,
        uint256 value,
        uint256 created
    );

    event UserAddedToPool (
        address indexed _userAddress,
        address indexed _partnerAddress,
        address indexed _pool,
        uint256 created
    );

    event UserSetBackupWithdrawalAddress (
        address indexed _userAddress,
        address indexed _userBackupAddress,
        address indexed _pool,
        uint256 created
    );

    event UserChangedToWithdrawalAddress (
        address indexed _userAddress,
        address indexed _userNewAddress,
        address indexed _pool,
        uint256 created
    );

    event UserDepositTokensWithdrawal (
        address indexed _userAddress,
        uint256 amount,
        uint256 tokenAmount,
        uint256 created
    );

    event Transferred (
        address indexed _from,
        address indexed _to,
        bytes32 indexed _typeOf,
        uint256 value,
        uint256 created
    );


    event FlagUint (
        uint256 flag
    );

    event FlagAddress (
        address flag
    );

    event UpdateAutherMemberLog (
        address indexed _operator,
        uint256 amount,
        uint256 created
    );

    /*** Modifiers *************/
    modifier ownerOnly() {
        require(msg.sender == initialOwner);
        _;
    }

    // 要求发布内容的地址锁定 pst 超过 300
    modifier autherOnly(address _address) {
        assert(autherStorage[_address] > 0);
        _;
    }

    modifier incentivesOperatorsOnly () {
        require(incentivesOperators[msg.sender] == true);
        _;
    }

    /*** Constructor *************/
    function PrimasToken(address _primasStorageAddress) public {
        initialAmount = 100000000 * 10 ** decimals;
        balances[msg.sender] = initialAmount;
        totalSupply = initialAmount;
        initialOwner = msg.sender;
        deployTime = block.timestamp;
        incentivesPool = 0;
        lastInflationDayStart = 0;

        primaStorage = PrimasStorageInterface(_primasStorageAddress);
    }

    /*** Methods *************/

    /* ====================== Inflation related methods ============================ */
    function inflate() ownerOnly() public returns (bool success) {

        uint currentTime = block.timestamp;

        uint currentDayStart = currentTime / 1 days;

        uint inflationAmount;

        if (lastInflationDayStart == currentDayStart) {
            return false;
        }

        lastInflationDayStart = currentDayStart;

        uint currentInflationRateDoubled = 20 - ( currentTime - deployTime ) / 1 years;

        if (currentInflationRateDoubled <= 0) {
            return false;
        }

        inflationAmount = initialAmount * currentInflationRateDoubled / 2;

        incentivesPool += inflationAmount;
        totalSupply += inflationAmount;

        return true;
    }

    function getIncentivesPool() view public returns (uint) {
        return incentivesPool;
    }

    /* ====================== Incentives related methods ============================ */
    function updateIncentivesOperator(address _operator, uint _value) ownerOnly() public returns (bool success) {
        require(incentivesOperators[_operator]);
        if (_value > 0) {
            incentivesOperators[_operator] = true;
            return true;
        } else {
            return false;
        }
    }

    function incentivesIn(uint _value) incentivesOperatorsOnly () public returns (bool success) {

        require(balances[msg.sender] >= _value && incentivesPool + _value > incentivesPool);

        balances[msg.sender] -= _value;
        incentivesPool += _value;

        return true;
    }

    function incentivesOut(uint _value) incentivesOperatorsOnly () public returns (bool success) {
        require(incentivesPool >= _value && balances[msg.sender] + _value > balances[msg.sender]);

        incentivesPool -= _value;
        balances[msg.sender] += _value;

        return true;
    }

    /* ====================== Incentives related methods ============================ */

    // 创建作者身份需锁定一定量的 PST
    function updateAutherMember(address _operator, uint _value) ownerOnly() public {

        require(_value >= 0);

        autherStorage[_operator] += _value;

        UpdateAutherMemberLog(_operator, _value, now);
    }

    // 查询作者身份
    function checkAutherRequire(address _autherAddress) view public returns(bool) {
        var amount = autherStorage[_autherAddress];
        if (amount >= 0) {
            return true;
        } else {
            return false;
        }
    }

    // 对于违规作者扣除 PST, 并将扣除的 PST 加入奖励池
    function penaltyAuther(address _fineUser, uint _fineAmount) public {
        var remainAmount = autherStorage[_fineUser];
        require(_fineAmount  <= remainAmount);
        autherStorage[_fineUser] -= _fineAmount;
        incentivesPool += _fineAmount;
    }

    // 对内容提供锁定 PST 功能
    function tokenTimelock(address _beneficiary, bytes32 _dna, uint _value, uint _releaseTime) ownerOnly() public {
        autherStorage[_beneficiary] += _value;
        articleStorage[_dna] = _value;
        tokenLockStorage[_dna] = now + _releaseTime * 1 days;
    }

    // 解锁 PST 锁定状态
    function releaseArticleLockToken(address _beneficiary, bytes32 _dna) ownerOnly() public {
        require(tokenLockStorage[_dna] > now);
        var lockAmount = articleStorage[_dna];
        articleStorage[_dna] -= lockAmount;
        autherStorage[_beneficiary] -= lockAmount;
    }

    /* ====================== Token related methods ============================ */

    function balanceOf(address _owner) constant public returns (uint256 balance) {
        var avaliableAmount = balances[_owner] - autherStorage[_owner];
        return avaliableAmount;
    }

    function transfer(address _to, uint256 _value) public returns (bool success) {
        require(balances[msg.sender] - autherStorage[msg.sender] >= _value && balances[_to] + _value > balances[_to]);
        balances[msg.sender] -= _value;
        balances[_to] += _value;
        Transfer(msg.sender, _to, _value);
        return true;
    }

}
